document.addEventListener("DOMContentLoaded",(function(){document.querySelectorAll('#site-main section[class*="theme--"]').forEach((function(e){var t=e.closest(".shopify-section"),c=Array.from(e.classList).find((e=>e.startsWith("theme--"))).replace("theme--","section-theme--");c&&t.classList.add(c)}));const e=document.querySelectorAll(".accordion"),t=e=>{const t=e.querySelector(".item__content");e.classList.remove("state--active"),t.style.maxHeight=null};e.forEach((e=>{const c=e.querySelectorAll(".item");c.forEach((e=>{const s=e.querySelector(".item__heading");e.querySelector(".item__content");s.onclick=()=>{e.classList.contains("state--active")?t(e):(c.forEach((c=>{c!==e&&t(c)})),(e=>{const t=e.querySelector(".item__content");e.classList.add("state--active"),t.style.maxHeight=t.scrollHeight+"px"})(e))}}))}))}));