document.addEventListener("DOMContentLoaded",(function(){document.querySelectorAll('#site-main section[class*="theme--"]').forEach((function(e){var t=e.closest(".shopify-section"),c=Array.from(e.classList).find((e=>e.startsWith("theme--"))).replace("theme--","section-theme--");c&&t.classList.add(c)}));document.querySelectorAll(".accordion").forEach((function(e){const t=e.querySelectorAll(".item");t.forEach((function(e){const c=e.querySelector(".item__heading"),s=e.querySelector(".item__content");c.addEventListener("click",(function(){e.classList.contains("state--active")?(e.classList.remove("state--active"),s.style.display="none"):(t.forEach((function(e){e.classList.remove("state--active"),e.querySelector(".item__content").style.display="none"})),e.classList.add("state--active"),s.style.display="block")}))}))}))}));