document.addEventListener("DOMContentLoaded",(function(){document.querySelectorAll('#site-main section[class*="theme--"]').forEach((function(e){var t=e.closest(".shopify-section"),c=Array.from(e.classList).find((e=>e.startsWith("theme--"))).replace("theme--","section-theme--");c&&t.classList.add(c)}));const e=document.querySelectorAll(".accordion"),t=e=>{const t=e.querySelector(".accordion__content");e.classList.remove("accordion__active"),t.style.maxHeight=null};e.forEach((c=>{const o=c.querySelector(".accordion__intro"),n=c.querySelector(".accordion__content");o.onclick=()=>{n.style.maxHeight?t(c):(e.forEach((e=>{e!==c&&t(e)})),(e=>{const t=e.querySelector(".accordion__content");e.classList.add("accordion__active"),t.style.maxHeight=t.scrollHeight+"px"})(c))}}))}));