document.addEventListener("DOMContentLoaded",(function(){const e=document.querySelectorAll(".grid__item"),t=e=>{const t=document.getElementsByClassName("grid--product")[0],n=parseInt(window.getComputedStyle(t).getPropertyValue("grid-auto-rows")),o=parseInt(window.getComputedStyle(t).getPropertyValue("grid-row-gap")),d=Math.ceil((e.querySelector(".content").getBoundingClientRect().height+o)/(n+o));e.style.gridRowEnd="span "+d};e.forEach((e=>{t(e),e.addEventListener("load",(()=>{t(e)}))})),window.addEventListener("resize",(()=>{e.forEach((e=>{t(e)}))}))}));